          <input
            name="benName"
            placeholder="benName"
            value={this.state.benName}
            onChange={this.handleInputChange}
          />{" "}
          <br />
          {/*  */}
          <input
            name="amount"
            placeholder="amount"
            value={this.state.amount}
            onChange={this.handleInputChange}
          />{" "}
          <br />
          {/*  */}
          <button type="submit">Submit</button>
        </form>
      </div>
    );
  }
}
export default TransactionForm;
